children.onClick:
{{
  (async () => {
    try {
      const res = await SIGCO_Create_PDHeader.run();
      const created = res?.data || res?.body?.data || res?.body || res;

      const pd_id = created?.pd_id;
      const fecha_pd = created?.fecha_pd; // ideal: YYYY-MM-DD

      if (!pd_id) {
        showAlert("No pude obtener pd_id del nuevo parte.", "error");
        return;
      }

      // 1) Set CURRENT
      await storeValue("current_pd_id", pd_id, true);
      await storeValue("current_pd", created || null, true);

      // 2) Set PREV (por fecha del nuevo parte)
      // Si la API no devolvi fecha_pd, caemos a hoy (o a tu widget de fecha si uss uno en Home)
      const fechaRef = fecha_pd || moment().format("YYYY-MM-DD");

      const prevRes = await SIGCO_Get_Prev_PDHeader.run({ fecha_pd: fechaRef });
      const prevRow =
        (prevRes?.data && prevRes.data[0]) ||
        (prevRes?.body?.data && prevRes.body.data[0]) ||
        null;

      await storeValue("prev_pd_id", prevRow?.pd_id || null, true);
      await storeValue("prev_pd", prevRow || null, true);

      // 3) Ir a Parte
      navigateTo("Parte");
    } catch (e) {
      console.error(e);
      showAlert("Error al crear Parte: " + (e?.message || e), "error");
    }
  })()
}}

