{{
  (async () => {
    const pdId = appsmith.store.pd_to_delete || tblPartes.selectedRow?.pd_id;

    if (!pdId) {
      showAlert("No hay pdId para borrar. Seleccion un Parte primero.", "warning");
      return;
    }

    try {
      await storeValue("pd_to_delete", pdId, true);

      // 1) Listar file_ids asociados al Parte (antes de borrar)
      const r = await DX_List_FileIds_By_PD.run();
      const rows = r?.data || r?.body?.data || [];

      let fileIds = (rows || [])
        .map(x => x.file_id?.id || x.file_id)
        .filter(Boolean);

      fileIds = Array.from(new Set(fileIds));

      // DEBUG recomendado 1 vez
      // showAlert("Se borrarn files: " + fileIds.length, "info");

      // 2) Borrar el header (cascada borra produccion + fotos puente)
      await SIGCO_Delete_PDHeader.run();

      // 3) Borrar files en Directus (DB + disco)
      if (fileIds.length > 0) {
        // uno por uno (ms robusto)
        for (const id of fileIds) {
          await DX_Delete_File_By_Id.run({ file_id: id });
        }
      }

      // 4) Refrescar UI
      await SIGCO_List_PDHeader.run();

      closeModal("mdlConfirmDeleteParte");
      showAlert("Parte eliminado (incluye fotos).", "success");
    } catch (e) {
      console.error(e);
      showAlert("Error al eliminar Parte/Fotos: " + (e?.message || e), "error");
    }
  })()
}}

