jsonPathKeys:
async () => {
  await Auth.guard();
  if (!appsmith.store.current_pd_id) {
    showAlert("No hay Parte seleccionado. Volviendo a Home.", "warning");
    navigateTo("Home");
    return;
  }
  const res = await SIGCO_Get_PDHeader.run();
  const row = res?.data || res?.body?.data || res?.body || res;
  if (!row) {
    showAlert("No se pudo cargar el header del parte.", "error");
    return;
  }
  await storeValue("current_pd", row, true);
  await storeValue("current_pd_id", row.pd_id, true);
  await PrevPD.refreshPrevFromCurrent();
  await PD.refresh();
  await Restricciones.refresh();
  const DEF_ENTRADA = "07:00:00";
  const DEF_SALIDA = "19:00:00";
  const DEF_CLIMA = "Despejado";
  const fullName = `${appsmith.store.me?.first_name || ""} ${appsmith.store.me?.last_name || ""}`.trim();
  const fallbackResponsable = fullName || (appsmith.store.me?.email || "");
  dpFechaPd.setValue(row.fecha_pd ? moment(row.fecha_pd).toDate() : moment().toDate());
  inpResponsable.setValue(row.responsable && row.responsable.trim() ? row.responsable : fallbackResponsable);
  tpEntrada.setValue(row.hora_entrada || DEF_ENTRADA);
  tpSalida.setValue(row.hora_salida || DEF_SALIDA);
