{{
  (async () => {
    try {
      const currentId = appsmith.store.current_pd_id;
      if (!currentId) {
        showAlert("No hay Parte activo.", "error");
        return;
      }

      // Helper: unwrap Directus item
      const unwrapItem = (res) => {
        const r = res?.data ?? res?.body ?? res;
        return r?.data ?? r;
      };// si viene {data:{}devuelve el item; si ya viene item, lo deja

      // Helper: unwrap Directus list
      const unwrapList = (res) => {
        const r = res?.data ?? res?.body ?? res;
        return r?.data ?? r; // si viene {data:[]} devuelve array
      };

      // Bloqueo simple por estado (si ya lo tens en store)
      if (appsmith.store.current_pd_estado === "CERRADO") {
        showAlert("El Parte est CERRADO. Reabrilo para editar.", "error");
        return;
      }

      // 1) Guardar cambios de header
      await SIGCO_Update_PDHeader.run();

      // 2) Refrescar header (y store) - OJO: Directus unwrap
      const res = await SIGCO_Get_PDHeader.run();
      const header = unwrapItem(res);

      if (header?.pd_id) {
        await storeValue("current_pd", header, true);
        await storeValue("current_pd_estado", header.estado || "BORRADOR", true);
      } else {
        showAlert("No pude refrescar el header luego de guardar.", "warning");
      }

      // 3) Recalcular PREV por la fecha actual del header (por si el usuario la edit)
      const fechaRef =
        header?.fecha_pd ||
        (dpFechaPd.selectedDate
          ? moment(dpFechaPd.selectedDate).format("YYYY-MM-DD")
          : null) ||
        moment().format("YYYY-MM-DD");

      const prevRes = await SIGCO_Get_Prev_PDHeader.run({ fecha_pd: fechaRef });
      const prevList = unwrapList(prevRes);
      const prevRow = Array.isArray(prevList) ? prevList[0] : null;

      await storeValue("prev_pd_id", prevRow?.pd_id || null, true);
      await storeValue("prev_pd", prevRow || null, true);

      showAlert("Header actualizado (prev recalculado).", "success");
    } catch (e) {
      console.error(e);
      showAlert("Error al guardar header: " + (e?.message || e), "error");
    }
  })()
}}

