async function () {
  const auth = appsmith.store.auth;
  if (!auth?.refresh_token) throw new Error("No hay refresh_token en store.");
  const res = await Directus_Refresh.run();
  console.log("REFRESH_RES", res);
  const payload = res?.data || res?.body?.data || res?.body || res;
  if (!payload?.access_token || !payload?.refresh_token) {
    throw new Error("Refresh OK pero no llegaron tokens. Revis mode: 'json'.");
  }
  const newAuth = {
    access_token: payload.access_token,
    refresh_token: payload.refresh_token,
    expires: payload.expires,
    obtained_at: Date.now()
  };
  await storeValue("auth", newAuth, true);
  return newAuth.access_token;
}

