async function () {
  if (!Auth.isLoggedIn()) {
    navigateTo("Login");
    return;
  }
  if (Auth.isTokenExpired(30)) {
    try {
      await Auth.refresh();
    } catch (e) {
      console.log("GUARD_REFRESH_ERROR", e);
      await Auth.logout(false);
      navigateTo("Login");
      return;
    }
  }
  if (!appsmith.store.me) {
    try {
      const meRes = await Directus_Me.run();
      const me = meRes?.data || meRes?.body?.data || meRes?.body || meRes;
      await storeValue("me", me ?? null, true);
    } catch (e) {
      console.log("GUARD_ME_ERROR", e);
      await Auth.logout(false);
      navigateTo("Login");
    }
  }
}

