async () => {
  try {
    if (!tblProd.selectedRow?.id_registro) {
      showAlert("Seleccion una lnea de Produccin (tblProd).", "warning");
      return;
    }
    const produccion_id = tblProd.selectedRow.id_registro;
    const files = fpProdFotos.files || [];
    if (!files.length) {
      showAlert("Seleccion al menos una foto.", "warning");
      return;
    }
    const desc = (inpFotoDesc.text || "").trim();
    for (let i = 0; i < files.length; i++) {
      await storeValue("upload_idx", i, true);
      const up = await DX_Upload_File_ByIdx.run();
      const file_id = ProdFotos._fileIdFromUpload(up);
      if (!file_id) throw new Error("No se obtuvo file_id del upload.");
      await SIGCO_Create_ProdFoto.run({
        produccion_id,
        file_id,
        descripcion: desc,
        orden: i + 1
      });
    }
    await SIGCO_List_ProdFotos.run();
    resetWidget("fpProdFotos");
    inpFotoDesc.setValue("");
    showAlert("Fotos cargadas.", "success");
  } catch (e) {
    console.error(e);
    showAlert("Error al cargar fotos: " + (e?.message || e), "error");
  }
}

