async () => {
  try {
    const rows = SIGCO_List_ProdFotos.data?.data || [];
    if (!rows.length) return;
    for (const r of rows) {
      const linkId = r?.id_registro;
      const fileId = r?.file_id?.id || r?.file_id;
      if (linkId) await SIGCO_Delete_ProdFoto.run({
        id: linkId
      });
      if (fileId) await DX_Delete_File.run({
        file_id: fileId
      });
    }
    await SIGCO_List_ProdFotos.run();
  } catch (e) {
    console.error(e);
    throw e;
  }
}

