export default {
  _fileIdFromUpload: (r) => r?.data?.id || r?.data?.data?.id || r?.id || null,

  uploadMany: async () => {
    try {
      if (!tblProd.selectedRow?.id_registro) {
        showAlert("Seleccion una lnea de Produccin (tblProd).", "warning");
        return;
      }

      const produccion_id = tblProd.selectedRow.id_registro;
      const files = fpProdFotos.files || [];

      if (!files.length) {
        showAlert("Seleccion al menos una foto.", "warning");
        return;
      }

      const desc = (inpFotoDesc.text || "").trim();

      // Subir + linkear 1 por 1
      for (let i = 0; i < files.length; i++) {
        await storeValue("upload_idx", i, true);

        const up = await DX_Upload_File_ByIdx.run();
        const file_id = this._fileIdFromUpload(up);

        if (!file_id) throw new Error("No se obtuvo file_id del upload.");

        await SIGCO_Create_ProdFoto.run({
          produccion_id,
          file_id,
          descripcion: desc,
          orden: i + 1
        });
      }

      await SIGCO_List_ProdFotos.run();

      resetWidget("fpProdFotos");
      inpFotoDesc.setValue("");

      showAlert("Fotos cargadas.", "success");
    } catch (e) {
      console.error(e);
      showAlert("Error al cargar fotos: " + (e?.message || e), "error");
    }
  },
  deleteOne: async (row) => {
    try {
      const linkId = row?.id_registro;
      const fileId = row?.file_id;

      if (!linkId || !fileId) {
        showAlert("Fila invlida para borrar.", "error");
        return;
      }

      // 1) borrar vnculo en tu coleccin
      await SIGCO_Delete_ProdFoto.run({ id: linkId });

      // 2) borrar archivo fsico en Directus
      await DX_Delete_File.run({ file_id: fileId });

      // 3) refrescar lista
      await SIGCO_List_ProdFotos.run();

      showAlert("Foto eliminada.", "success");
    } catch (e) {
      console.error(e);
      showAlert("No se pudo eliminar la foto.", "error");
    }
  },

  purgeAllForSelectedProduction: async () => {
    try {
      // usa el filtro de SIGCO_List_ProdFotos basado en la produccin seleccionada
      const rows = (SIGCO_List_ProdFotos.data?.data || []);

      if (!rows.length) return;

      for (const r of rows) {
        const linkId = r?.id_registro;
        const fileId = r?.file_id?.id || r?.file_id;

        if (linkId) await SIGCO_Delete_ProdFoto.run({ id: linkId });
        if (fileId) await DX_Delete_File.run({ file_id: fileId });
      }

      await SIGCO_List_ProdFotos.run();
    } catch (e) {
      console.error(e);
      throw e;
    }
  }
};

