      await DX_Delete_File.run({ file_id: fileId });

      // 3) refrescar lista
      await SIGCO_List_ProdFotos.run();

      showAlert("Foto eliminada.", "success");
    } catch (e) {
      console.error(e);
      showAlert("No se pudo eliminar la foto.", "error");
    }
  },

  purgeAllForSelectedProduction: async () => {
    try {
      // usa el filtro de SIGCO_List_ProdFotos basado en la produccin seleccionada
      const rows = (SIGCO_List_ProdFotos.data?.data || []);

      if (!rows.length) return;

      for (const r of rows) {
        const linkId = r?.id_registro;
        const fileId = r?.file_id?.id || r?.file_id;

        if (linkId) await SIGCO_Delete_ProdFoto.run({ id: linkId });
        if (fileId) await DX_Delete_File.run({ file_id: fileId });
      }

      await SIGCO_List_ProdFotos.run();
    } catch (e) {
      console.error(e);
      throw e;
    }
  }
};

