export default {
  // Cambi esto si dentro de Appsmith no resuelve "directus"
  // Alternativas: "http://erp_directus:8055" (por container_name)
  directusBaseUrl: "http://directus:8055",

  isLoggedIn() {
    return !!appsmith.store.auth?.access_token;
  },

  // Token vencido (o por vencer) usando expires + obtained_at
  isTokenExpired(marginSeconds = 30) {
    const auth = appsmith.store.auth;
    if (!auth?.access_token || !auth?.expires || !auth?.obtained_at) return true;

    const now = Date.now();
    const expiryMs = auth.obtained_at + auth.expires * 1000;
    return now >= expiryMs - marginSeconds * 1000;
  },

  async login() {
    try {
      await storeValue("auth_error", null, true);

      // 1) Login en Directus
      const res = await Directus_Login.run();
      console.log("LOGIN_RES", res);

      // 2) Soportar distintas formas de respuesta segn Appsmith/datasource
      const payload = res?.data || res?.body?.data || res?.body || res;

      if (!payload?.access_token || !payload?.refresh_token) {
        throw new Error(
          "No llegaron tokens. Revis que Directus_Login incluya mode: 'json' y que el response tenga access_token/refresh_token."
        );
      }

      const auth = {
        access_token: payload.access_token,
        refresh_token: payload.refresh_token,
        expires: payload.expires,   // segundos
        obtained_at: Date.now()     // ms
      };

      // 3) Persistir tokens en el store (persist=true)
      await storeValue("auth", auth, true);

      // 4) Traer perfil del usuario (opcional recomendado)
      try {
        const meRes = await Directus_Me.run();
        const me = meRes?.data || meRes?.body?.data || meRes?.body || meRes;
        await storeValue("me", me ?? null, true);
      } catch (e) {
        // No bloqueamos login si /users/me falla, pero dejamos aviso
        console.log("ME_ERROR", e);
      }

      // 5) Ir a Home (el nombre debe coincidir con el de la pgina)
      navigateTo("Home");
    } catch (e) {
      const msg = e?.message || "Error de login";
      await storeValue("auth_error", msg, true);
      showAlert(msg, "error");
    }
  },

  async refresh() {
    const auth = appsmith.store.auth;
    if (!auth?.refresh_token) throw new Error("No hay refresh_token en store.");

    const res = await Directus_Refresh.run();
    console.log("REFRESH_RES", res);

    const payload = res?.data || res?.body?.data || res?.body || res;

    if (!payload?.access_token || !payload?.refresh_token) {
      throw new Error("Refresh OK pero no llegaron tokens. Revis mode: 'json'.");
    }

    const newAuth = {
      access_token: payload.access_token,
      refresh_token: payload.refresh_token,
      expires: payload.expires,
      obtained_at: Date.now()
    };

    await storeValue("auth", newAuth, true);
    return newAuth.access_token;
  },

  async guard() {
    // Si no hay sesin, ir a Login
    if (!this.isLoggedIn()) {
      navigateTo("Login");
      return;
    }

    // Si el token est vencido o por vencer, refrescar
    if (this.isTokenExpired(30)) {
      try {
        await this.refresh();
      } catch (e) {
        console.log("GUARD_REFRESH_ERROR", e);
        await this.logout(false);
        navigateTo("Login");
        return;
      }
    }

    // Si no tenemos /me, lo buscamos
    if (!appsmith.store.me) {
      try {
        const meRes = await Directus_Me.run();
        const me = meRes?.data || meRes?.body?.data || meRes?.body || meRes;
        await storeValue("me", me ?? null, true);
      } catch (e) {
        console.log("GUARD_ME_ERROR", e);
        await this.logout(false);
        navigateTo("Login");
      }
    }
  },

  async logout(callApi = true) {
    try {
      if (callApi && appsmith.store.auth?.refresh_token && typeof Directus_Logout !== "undefined") {
        await Directus_Logout.run();
      }
    } catch (e) {
      console.log("LOGOUT_ERROR", e);
    } finally {
      await clearStore();
    }
  }
};

