export default {
  toNum(x) {
    const n = Number(x);
    return Number.isFinite(n) ? n : null;
  },

  buildHHItems() {
    const currentPdId = appsmith.store.current_pd_id;
    const rows = SIGCO_Source_List_HH_By_PD.data?.data || [];

    return rows
      .map(r => ({
        pd_id: currentPdId,
        persona_nombre: r.persona_nombre,
        categoria_id: r.categoria_id,
        horas: this.toNum(r.horas),
        area_codigo: r.area_codigo || null,
        observacion: r.observacion || null,
      }))
      .filter(i => i.pd_id && i.persona_nombre && i.categoria_id && i.horas !== null);
  },

  buildHMItems() {
    const currentPdId = appsmith.store.current_pd_id;
    const rows = SIGCO_Source_List_HM_By_PD.data?.data || [];

    return rows
      .map(r => ({
        pd_id: currentPdId,
        equipo_id: r.equipo_id,
        horas_maquina: this.toNum(r.horas_maquina),
        area_codigo: r.area_codigo || null,
        observacion: r.observacion || null,
      }))
      .filter(i => i.pd_id && i.equipo_id && i.horas_maquina !== null);
  },

  async copyHHFromPrev() {
  await PrevPD.refreshPrevFromCurrent();
    const currentPdId = appsmith.store.current_pd_id;
    const prevPdId = appsmith.store.prev_pd_id;

    if (!currentPdId) {
      showAlert("No hay current_pd_id (Parte actual).", "warning");
      return;
    }
    if (!prevPdId) {
      showAlert("No hay prev_pd_id (Parte anterior).", "warning");
      return;
    }

    await SIGCO_Source_List_HH_By_PD.run();

    const items = this.buildHHItems();
    if (items.length === 0) {
      showAlert("El Parte anterior no tiene HH para copiar.", "info");
      return;
    }

    await SIGCO_Bulk_Create_HH.run({ items });
  // refrescar tabla del Parte ACTUAL
  await SIGCO_List_HH.run();
  await SIGCO_Sum_HH.run();

  showAlert(`HH copiado: ${items.length} filas.`, "success");
  },

  async copyHMFromPrev() {
  await PrevPD.refreshPrevFromCurrent();
    const currentPdId = appsmith.store.current_pd_id;
    const prevPdId = appsmith.store.prev_pd_id;

    if (!currentPdId) {
      showAlert("No hay current_pd_id (Parte actual).", "warning");
      return;
    }
    if (!prevPdId) {
      showAlert("No hay prev_pd_id (Parte anterior).", "warning");
      return;
    }

    await SIGCO_Source_List_HM_By_PD.run();

    const items = this.buildHMItems();
    if (items.length === 0) {
      showAlert("El Parte anterior no tiene HM para copiar.", "info");
      return;
    }

    await SIGCO_Bulk_Create_HM.run({ items });
    await SIGCO_List_HM.run();
  await SIGCO_Sum_HM.run();

    showAlert(`HM copiado: ${items.length} filas.`, "success");
  },
};

