export default {
  // normaliza cualquier input a YYYY-MM-DD
  toISODate(d) {
    if (!d) return null;
    return moment(d).format("YYYY-MM-DD");
  },

  async refreshPrevFromCurrent() {
    // 1) saco la fecha del header actual (fuente de verdad)
    const current = appsmith.store.current_pd;
    const currentFecha = current?.fecha_pd;

    const fechaISO = this.toISODate(currentFecha);
    if (!fechaISO) {
      await storeValue("prev_pd_id", null, true);
      showAlert("No pude determinar fecha_pd del Parte actual (current_pd.fecha_pd).", "warning");
      return null;
    }

    // 2) busco el parte anterior por fecha
    const res = await SIGCO_Get_Prev_PDHeader.run({ fecha_pd: fechaISO });
    const row = (res?.data?.[0]) || (res?.body?.data?.[0]) || null;

    // 3) CLAVE: siempre pisar el store, aunque sea null
    await storeValue("prev_pd_id", row?.pd_id || null, true);
    await storeValue("prev_pd_fecha", row?.fecha_pd || null, true);

    return row;
  }
};

