export default {
  _unwrap(res) {
    // Soporta: Appsmith wrapper / directus raw / body
    const r = res?.data ?? res?.body ?? res;
    return r?.data ?? r;
  },

  _actor() {
    // Ajust si vos guards el usuario en otro lado
    return (
      appsmith.store.auth?.user?.email ||
      appsmith.store.me?.email ||
      appsmith.store.auth?.user?.id ||
      "appsmith"
    );
  },

  async refresh() {
    const res = await SIGCO_Get_PDHeader.run();
    const header = this._unwrap(res);

    if (!header?.pd_id) {
      showAlert("No pude leer el header del Parte (pd_header).", "error");
      return null;
    }

    await storeValue("current_pd", header, true);
    await storeValue("current_pd_estado", header.estado || "BORRADOR", true);

    return header;
  },

  async close() {
    if (!appsmith.store.current_pd_id) {
      showAlert("No hay Parte activo (current_pd_id).", "error");
      return;
    }

    // Aseguro estado actualizado
    if (!appsmith.store.current_pd_estado) await this.refresh();

    if (appsmith.store.current_pd_estado === "CERRADO") {
      showAlert("El Parte ya est CERRADO.", "info");
      return;
    }

    await SIGCO_Close_PDHeader.run({ cerrado_por: this._actor() });
    await this.refresh();
    showAlert("Parte CERRADO.", "success");
  },

  async reopen() {
    if (!appsmith.store.current_pd_id) {
      showAlert("No hay Parte activo (current_pd_id).", "error");
      return;
    }

    if (!appsmith.store.current_pd_estado) await this.refresh();

    if (appsmith.store.current_pd_estado !== "CERRADO") {
      showAlert("El Parte no est cerrado (no hace falta reabrir).", "info");
      return;
    }

    await SIGCO_Reopen_PDHeader.run({ reabierto_por: this._actor() });
    await this.refresh();
    showAlert("Parte REABIERTO (BORRADOR).", "success");
  }
};

